allow:
Bash(powershell.exe -NoProfile -Command "& {
    $electronCache = ''$env:LOCALAPPDATA\\electron\\Cache''
    if \(Test-Path $electronCache\) {
        Write-Host ''Electron cache found at:'' $electronCache
        Get-ChildItem $electronCache -Recurse -Force | Where-Object { $_Name -eq ''nul'' } | ForEach-Object { Write-Host ''Found nul file:'' $_FullName }
    }

    $electronBuilderCache = ''$env:LOCALAPPDATA\\electron-builder\\Cache''
    if \(Test-Path $electronBuilderCache\) {
        Write-Host ''Electron-builder cache found at:'' $electronBuilderCache
        Get-ChildItem $electronBuilderCache -Recurse -Force -ErrorAction SilentlyContinue | Where-Object { $_Name -eq ''nul'' -or $_Name -eq ''python'' } | ForEach-Object { Write-Host ''Found:'' $_FullName }
    }
}")
